/*Table structure for table `fly_member` */

DROP TABLE IF EXISTS `fly_member`;

CREATE TABLE `fly_member` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `catid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '用户组id',
  `username` varchar(100) NOT NULL DEFAULT '' COMMENT '用户名',
  `password` varchar(100) NOT NULL DEFAULT '' COMMENT '密码',
  `nickname` varchar(50) NOT NULL DEFAULT '' COMMENT '昵称',
  `truename` varchar(100) NOT NULL DEFAULT '' COMMENT '真实姓名',
  `gender` tinyint(1) NOT NULL DEFAULT '0' COMMENT '性别',
  `email` varchar(100) NOT NULL DEFAULT '' COMMENT '邮箱',
  `tel` varchar(100) NOT NULL DEFAULT '' COMMENT '电话',
  `address` varchar(255) NOT NULL DEFAULT '' COMMENT '地址',
  `sign` varchar(255) NOT NULL DEFAULT '' COMMENT '个性签名',
  `regtime` int(11) NOT NULL DEFAULT '0' COMMENT '注册时间',
  `lastlogtime` int(11) NOT NULL DEFAULT '0' COMMENT '最后登录时间',
  `logins` int(11) NOT NULL DEFAULT '0' COMMENT '登录次数',
  `status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态',
  `thumb` varchar(100) NOT NULL DEFAULT '' COMMENT '头像',
  `tips` varchar(100) NOT NULL DEFAULT '' COMMENT '备注',
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`),
  UNIQUE KEY `email` (`email`)
) ENGINE=MyISAM AUTO_INCREMENT=44 DEFAULT CHARSET=utf8 COMMENT='会员表';

