/*Table structure for table `fly_product` */

DROP TABLE IF EXISTS `fly_product`;

CREATE TABLE `fly_product` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `addtime` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `sort` tinyint(4) unsigned NOT NULL DEFAULT '100' COMMENT '排序',
  `status` tinyint(4) unsigned NOT NULL DEFAULT '1' COMMENT '状态',
  `visits` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览数',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `catid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '分类',
  `gender` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '性别 0女 1男 2保密',
  `city` varchar(50) DEFAULT '' COMMENT '城市名称',
  `cityid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '城市id',
  `birthday` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '生日 时间戳',
  `week` varchar(100) NOT NULL DEFAULT '' COMMENT '星期几 多选',
  `thumb` varchar(100) NOT NULL DEFAULT '' COMMENT '缩略图',
  `photos` varchar(500) NOT NULL DEFAULT '' COMMENT '多图',
  `longlat` varchar(50) NOT NULL DEFAULT '' COMMENT '经纬度',
  `content` text NOT NULL COMMENT '内容',
  `content2` text NOT NULL COMMENT '内容2',
  `audio` varchar(50) NOT NULL DEFAULT '' COMMENT '音频',
  `video` varchar(50) NOT NULL DEFAULT '' COMMENT '视频',
  `options` varchar(500) NOT NULL DEFAULT '' COMMENT '参数多个，json格式',
  PRIMARY KEY (`id`)
